import axios from 'axios';
import { CUSTOMER_EMAIL, FEEDBACK_EMAIL, REGISTER_REQ } from './types';

const headers = {
    headers: {
        'Access-Control-Allow-Origin': '*'
    }
};

export default function deleteFile(action, email, id, fullname, message) {
    if (action === CUSTOMER_EMAIL) {
        return new Promise((resolve, reject) => {
            const api_uri = 'http://localhost:8080/GatewayApi/email/customer/';
            axios.put(api_uri, {
                email,
                id,
                headers,
                json: true
            })
            .then(res => {
                if (res.data.hasBeenSuccessful === true){
                    resolve();
                }
                resolve();
            })
            .catch(error => reject(error.message));
        });
    } else if (action === FEEDBACK_EMAIL) {
        console.log('feedback');
    }
}
