import axios from 'axios';
import setAuthorizationHeader from './../utils/setAuthorizationToken';


const headers = {
    headers: {
        'Access-Control-Allow-Origin': '*'
    }
};

export default function(token) {
    return new Promise((resolve, reject) => {
        let redirect;
        const api_uri = 'http://localhost:8080/GatewayApi/users/tokenUpdate';
        axios.post(api_uri, {
            token,
            headers,
            json: true
        })
        .then(res => {
            const respJS = JSON.parse(res.data.content.text);
            if (respJS.hasBeenSuccessful === true){
                redirect = true;
                const token = respJS.content.token;
                localStorage.setItem('jwtToken', token);
                setAuthorizationHeader(token);
            }
            resolve([redirect]);
        })
        .catch(error => reject(error.message));
    });
}
