import axios from 'axios';
import { SIGN_TOKEN_REQ } from './../actions/types';

let headers = {
    headers: {
        'Access-Control-Allow-Origin': '*'
    }
};

export default function SignToken(action, token, user) {
    if (action === SIGN_TOKEN_REQ) {
        headers.headers['Authorization'] = token;
        return new Promise((resolve, reject) => {
            let newToken;
            const api_uri = 'http://localhost:8080/GatewayApi/users/signToken';
            axios({
                method: 'post',
                url: api_uri,
                data: user,
                config: { headers },
                json: true
            })
            .then(res => {
                if (res.data.hasBeenSuccessful === true){
                    newToken = res.data.content.token;
                }
                resolve(newToken);
            })
            .catch(error => reject(error.message));
        });
    }
}
